import React, { useState, useEffect } from "react";
import axios from "axios";

const KanbanBoard = () => {
  debugger
  const [tickets, setTickets] = useState([]);
  const [groupBy, setGroupBy] = useState("status"); // Default grouping by status
  const [sortBy, setSortBy] = useState("priority"); // Default sorting by priority

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await axios.get("https://api.quicksell.co/v1/internal/frontend-assignment");
        setTickets(response.data.tickets  );
      } catch (error) {
        console.error("Error fetching tickets", error);
      }
    };
    fetchTickets();

    // Restore saved state from localStorage
    const savedGroupBy = localStorage.getItem("groupBy");
    const savedSortBy = localStorage.getItem("sortBy");
    if (savedGroupBy) setGroupBy(savedGroupBy);
    if (savedSortBy) setSortBy(savedSortBy);
  }, []);

  useEffect(() => {
    // Save the current state to localStorage
    localStorage.setItem("groupBy", groupBy);
    localStorage.setItem("sortBy", sortBy);
  }, [groupBy, sortBy]);

  const groupTickets = (tickets) => {
    switch (groupBy) {
      case "status":
        return groupByStatus(tickets);
      case "user":
        return groupByUser(tickets);
      case "priority":
        return groupByPriority(tickets);
      default:
        return tickets;
    }
  };

  const sortTickets = (groupedTickets) => {
    return groupedTickets.map(group => ({
      ...group,
      tickets: group.tickets.sort((a, b) => {
        if (sortBy === "priority") {
          return b.priority - a.priority;
        } else if (sortBy === "title") {
          return a.title.localeCompare(b.title);
        }
        return 0;
      })
    }));
  };

  const groupedTickets = groupTickets(tickets);
  const sortedTickets = sortTickets(groupedTickets);

  return (
    <div>
      <GroupBySelector groupBy={groupBy} setGroupBy={setGroupBy} />
      <SortBySelector sortBy={sortBy} setSortBy={setSortBy} />
      <div className="kanban-board">
        {groupedTickets.map((group, idx) => (
          <div className="kanban-column" key={idx}>
            <h3>{group.name}</h3>
            {group.tickets.map((ticket) => (
              <div className="ticket" key={ticket.id}>
                <h4>{ticket.title}</h4>
                <p>Priority: {ticket.priority}</p>
                <p>Status: {ticket.status}</p>
              </div>
            ))}
          </div>
        ))}
      </div>  
    </div>
  );
};

// Grouping functions
const groupByStatus = (tickets) => {
  const grouped = {};

  tickets.forEach((ticket) => {
    const status = ticket.status;

    if (!grouped[status]) {
      grouped[status] = []; // Initialize an array for this status if not present
    }

    grouped[status].push(ticket); // Push the current ticket into the corresponding status group
  });

  // Convert the object into an array of groups with { name, tickets } for easier rendering
  return Object.keys(grouped).map((status) => ({
    name: status,
    tickets: grouped[status],
  }));
};


const groupByUser = (tickets) => {
  const grouped = {};

  tickets.forEach((ticket) => {
    const user = ticket.assignedUser;

    if (!grouped[user]) {
      grouped[user] = []; // Initialize an array for this user if not present
    }

    grouped[user].push(ticket); // Push the current ticket into the corresponding user group
  });

  // Convert the object into an array of groups with { name, tickets } for easier rendering
  return Object.keys(grouped).map((user) => ({
    name: user || "Unassigned", // Handle cases where there might not be an assigned user
    tickets: grouped[user],
  }));
};


const groupByPriority = (tickets) => {
  const priorityLevels = {
    4: "Urgent",
    3: "High",
    2: "Medium",
    1: "Low",
    0: "No Priority",
  };

  const grouped = {};

  tickets.forEach((ticket) => {
    const priority = ticket.priority;

    if (!grouped[priority]) {
      grouped[priority] = []; // Initialize an array for this priority if not present
    }

    grouped[priority].push(ticket); // Push the current ticket into the corresponding priority group
  });

  // Convert the object into an array of groups with { name, tickets } for easier rendering
  return Object.keys(grouped).map((priority) => ({
    name: priorityLevels[priority], // Get the friendly name of the priority level
    tickets: grouped[priority],
  }));
};


// Component for ticket display
const Ticket = ({ ticket }) => (
  <div className="ticket">
    <h4>{ticket.title}</h4>
    <p>Priority: {ticket.priority}</p>
    <p>Status: {ticket.status}</p>
  </div>
);

const GroupBySelector = ({ groupBy, setGroupBy }) => (
  <div>
    <label>Group By: </label>
    <select value={groupBy} onChange={e => setGroupBy(e.target.value)}>
      <option value="status">Status</option>
      <option value="user">User</option>
      <option value="priority">Priority</option>
    </select>
  </div>
);

const SortBySelector = ({ sortBy, setSortBy }) => (
  <div>
    <label>Sort By: </label>
    <select value={sortBy} onChange={e => setSortBy(e.target.value)}>
      <option value="priority">Priority</option>
      <option value="title">Title</option>
    </select>
  </div>
);

export default KanbanBoard;
